function R(min, max) { return min + (Math.floor(10 * Math.random() * (max - min)) / 10) };

var icons = $('.bgicon'), total = $('.bgicon').length;
var container = $(".bgicons"), w = container.innerWidth(), h = container.innerHeight();
var tweenNum = 50;

for (var i = 0; i < total; i++) {
    var icon = icons[i]
    // place icon 
    TweenLite.set(icon, { x: R(0, w), y: R(0, h), z: R(-200, 200) });
    // make icon rotate 
    TweenMax.to(icon, R(2, 8), { rotationX: R(0, 360), rotationY: R(0, 360), repeat: -1, yoyo: true, ease: Sine.easeInOut, delay: -5 });
    // make icon sway 
    TweenMax.to(icon, R(4, 8), { x: '+=' + R(50, 150), y: '+=' + R(50, 150), rotationZ: R(0, 180), repeat: -1, yoyo: true, ease: Sine.easeInOut })
}

$(".profile-img").paroller({
    factor: 0.2,            // multiplier for scrolling speed and offset
    factorXs: 0.1,           // multiplier for scrolling speed and offset
    type: 'foreground',     // background, foreground
    direction: 'vertical' // vertical, horizontal
});

$(window).scroll(function () {
    var light_pos = $('#contact').offset().top;
    var light_height = $('#contact').height();
    var menu_pos = $('.js-social-link .fab').offset().top;
    var scroll = $(window).scrollTop();

    if (menu_pos > light_pos && menu_pos < (light_pos + light_height)) {
        $('.js-social-link .fab').addClass('light');
    } else {
        $('.js-social-link .fab').removeClass('light');
    }
})
